        def step_down(curr,i):
            # ['0', '0', '0', '0']
            splitArray = list(curr)
            number = int(splitArray[i])
            number -= 1
            if number == -1:
                number = 9
            splitArray[i]= str(number)
            return ''.join(splitArray)


        start = '0000'
        count = 0
        # 1. [0000]->[1000,9000]
        # 2. [1000,9000] -> [2000,0000, 0000,8000]
        # 3. [2000,0000, 0000,8000] -> [3000,1000,1000,9000,1000,9000,9000,7000]
        visited = set()
        if start in deadends:
            return -1
        visited.add(start)
        for i in range(len(deadends)):
            visited.add(deadends[i])
