class Solution {
public:
    int openLock(vector<string>& deadends, string target) {
        deque<string> q;
        unordered_set<string> visited;
        q.push_back("0000");
        visited.insert("0000");
        unordered_set<string> obs(deadends.begin(), deadends.end());
        if (obs.count("0000")) return -1;
        int cnt = 0;
        while (!q.empty()) {
            int s = q.size();
            for (int k = 0; k < s; ++k) {
                auto t = q.front(); q.pop_front();
                if (t.compare(target) == 0) {
                    return cnt;
                }
                for (int i = 0; i < t.size(); ++i) {
                    for (int j = 0; j < move.size(); ++j) {
                        int digit = (t[i] - '0' + move[j] + 10) % 10;
                        string new_t = t;
                        new_t[i] = digit + '0';
                        if (visited.count(new_t) || obs.count(new_t)) {
                            continue;
                        }
                        q.push_back(new_t);
                        visited.insert(new_t);
                    }
                }
            }
            ++cnt;
        }
        return -1;
    }

    vector<int> move{1, -1};
};
