class Solution {
public:
    int openLock(vector<string>& deadends, string target) {
        const string start = "0000";
        unordered_set<string> dead(deadends.begin(), deadends.end());
        queue<string> q1{{start}}, q2{{target}};
        unordered_set<string> s1{start}, s2{target};
        int res1 = 0, res2 = 0;

        if (dead.count(start))
            return -1;
        if (target.compare(start) == 0)
            return 0;
        while (!q1.empty()) {
            res1++;
            int size = q1.size();
            while (size--) {
                string cur = q1.front();
                q1.pop();
                for (string s : transform(cur)) {
                    if (s2.count(s)) {
                        return res1 + res2;
                    }
                    if (!dead.count(s) && !s1.count(s)) {
                        q1.push(s);
                        s1.insert(s);
                    }
                }
            }
            swap(q1, q2);
            swap(s1, s2);
            swap(res1, res2);
        }
        return -1;
    }
private:
    vector<string> transform(string original) {
        vector<string> strings;
        for (int i = 0; i < original.length(); ++i) {
            char c = original[i];
            original[i] = move(c, 1);
            strings.push_back(original);
            original[i] = move(c, 0);
            strings.push_back(original);
            original[i] = c;
        }
        return strings;
    }
    char move(char c, bool forward) {
        if (forward) {
            if (c == '9') {
                return '0';
            } else {
                return c + 1;
            }
        } else {
            if (c == '0') {
                return '9';
            } else {
                return c - 1;
            }
        }
    }
};
