class Solution {
public:
    string moveUp(string cur, int location)
    {
        if(cur[location] == '9')
            cur[location] = '0';
        else
            cur[location] += 1;

        return cur;
    }

    string moveDown(string cur, int location)
    {
        if(cur[location] == '0')
            cur[location] = '9';
        else
            cur[location] -= 1;

        return cur;
    }

    int openLock(vector<string>& deadends, string target) {
        queue<string> q;
        set<string> visited;
        int steps = 0;

        q.push("0000");
        visited.insert("0000");

        while(!q.empty())
        {
            int len = q.size();

            for(int i = 0; i < len; i++)
            {
                string cur = q.front();
                q.pop();

                if(find(deadends.begin(), deadends.end(), cur) != deadends.end())
                    continue;

                if(cur ==  target)
                    return steps;

                for(int j = 0; j < 4; j++)
                {
                    string up = moveUp(cur, j);
                    if(visited.find(up) == visited.end())
                    {
                        q.push(up);
                        visited.insert(up);
                    }

                    string down = moveDown(cur, j);
                    if(visited.find(down) == visited.end())
                    {
                        q.push(down);
                        visited.insert(down);
                    }
                }
            }

            steps++;
        }

        return -1;
    }
};

