class Solution {
public:
    const int INF = 10e7;
    int dp[10][10][10][10];
    bool dead[10][10][10][10];

    int openLock(vector<string>& deadends, string target) {
        if(target == "0000") return 0;
        for(int a=0; a<10; a++)
            for(int b=0; b<10; b++)
                for(int c=0; c<10; c++)
                    for(int d=0; d<10; d++)
                        dead[a][b][c][d] = false;

        for(int a=0; a<10; a++)
            for(int b=0; b<10; b++)
                for(int c=0; c<10; c++)
                    for(int d=0; d<10; d++)
                        dp[a][b][c][d] = INF;


        for(int i=0; i<deadends.size(); i++)
            dead[deadends[i][0]-'0'][deadends[i][1]-'0'][deadends[i][2]-'0'][deadends[i][3]-'0'] = true;

        dp[0][0][0][0] = 0;
        dfs(0, 0, 0, 0);
        int realans = dp[target[0]-'0'][target[1]-'0'][target[2]-'0'][target[3]-'0'];
        return (realans < 0 || realans >= INF) ? -1 : realans;
    }

    void dfs(int a, int b, int c, int d){
        if(dead[a][b][c][d]) return;

        dp[a][b][c][d] = min(dp[a][b][c][d], 1 + dp[(a+1)%10][b][c][d]);
        dp[a][b][c][d] = min(dp[a][b][c][d], 1 + dp[(a+9)%10][b][c][d]);
        dp[a][b][c][d] = min(dp[a][b][c][d], 1 + dp[a][(b+1)%10][c][d]);
        dp[a][b][c][d] = min(dp[a][b][c][d], 1 + dp[a][(b+9)%10][c][d]);
        dp[a][b][c][d] = min(dp[a][b][c][d], 1 + dp[a][b][(c+1)%10][d]);
        dp[a][b][c][d] = min(dp[a][b][c][d], 1 + dp[a][b][(c+9)%10][d]);
        dp[a][b][c][d] = min(dp[a][b][c][d], 1 + dp[a][b][c][(d+1)%10]);
        dp[a][b][c][d] = min(dp[a][b][c][d], 1 + dp[a][b][c][(d+9)%10]);


        if(dp[(a+1)%10][b][c][d] > dp[a][b][c][d] + 1) {
            dfs((a+1)%10, b, c, d);
        }
        if(dp[(a+9)%10][b][c][d] > dp[a][b][c][d] + 1) {
            dfs((a+9)%10, b, c, d);
        }

        if(dp[a][(b+1)%10][c][d] > dp[a][b][c][d] + 1) {
            dfs(a, (b+1)%10, c, d);
        }
        if(dp[a][(b+9)%10][c][d] > dp[a][b][c][d] + 1) {
            dfs(a, (b+9)%10, c, d);
        }

        if(dp[a][b][(c+1)%10][d] > dp[a][b][c][d] + 1) {
            dfs(a, b, (c+1)%10, d);
        }
        if(dp[a][b][(c+9)%10][d] > dp[a][b][c][d] + 1) {
            dfs(a, b, (c+9)%10, d);
        }

        if(dp[a][b][c][(d+1)%10] > dp[a][b][c][d] + 1) {
            dfs(a, b, c, (d+1)%10);
        }
        if(dp[a][b][c][(d+9)%10] > dp[a][b][c][d] + 1) {
            dfs(a, b, c, (d+9)%10);
        }
    }

