```rust
pub fn open_lock(deadends: Vec<String>, target: String) -> i32 {
    use std::collections::{HashSet, VecDeque};
    let target: u32 = target.parse().unwrap();
    let mut deadends: HashSet<u32> = deadends.into_iter().map(|x| x.parse().unwrap()).collect();
    let mut s1: HashSet<u32> = HashSet::new();
    let mut s2: HashSet<u32> = HashSet::new();
    let mut q1 = VecDeque::new();
    let mut q2 = VecDeque::new();
    q1.push_back(0);
    q2.push_back(target);
    let mut steps = 0;
    while !q1.is_empty() {
        for i in (0..q1.len()) {
            let curr = q1.pop_front().unwrap();
            if s2.contains(&curr) {
                return steps - 1;
            }
            if deadends.contains(&curr) || !s1.insert(curr) {
                continue;
            }
            for i in [1, 10, 100, 1000] {
                let old = curr / i % 10;
                q1.push_back(curr - old * i + ((old + 1) % 10) * i);
                q1.push_back(curr - old * i + ((old + 9) % 10) * i);
            }
        }
        steps += 1;
        std::mem::swap(&mut s1, &mut s2);
        std::mem::swap(&mut q1, &mut q2);
    }
    -1
}
