	class Solution {
		public int openLock(String[] deadends, String target) {

			String source = "0000";
			HashSet<String> dead = new HashSet<>();
			LinkedList<Pair<String, Integer>> queue = new LinkedList<>();
			HashSet<String> seen = new HashSet<>();

			for(String str : deadends) {
				dead.add(str);
			}

			if (dead.contains(source)) {
				return -1;
			}

			queue.offer(new Pair(source, 0));
			seen.add(source);

			while (queue.size() > 0) {

				Pair<String,Integer> kv = queue.poll();
				String value = kv.getKey();
				int steps = kv.getValue();


				// check if value is target, if not add values adjacent values in direction of target
				if (value.equals(target)) return steps;


				steps++;


				for(int i = 0; i < value.length(); ++i) {
					StringBuilder sb = new StringBuilder(value);

					char c = value.charAt(i);

					// check if char is a wrap around
					if (c == '0') {
						sb.setCharAt(i, '9');
						addToQueue(queue, dead, sb, steps, seen);
						sb.setCharAt(i, '1');
						addToQueue(queue, dead, sb, steps, seen);
					} else if (c == '9') {
						sb.setCharAt(i, '0');
						addToQueue(queue, dead, sb, steps, seen);
						sb.setCharAt(i, '8');
						addToQueue(queue, dead, sb, steps, seen);
					} else {
						c++;
						sb.setCharAt(i, c);
						addToQueue(queue, dead, sb, steps, seen);
						c-=2;
						sb.setCharAt(i, c);
						addToQueue(queue, dead, sb, steps, seen);
					}
				}



			}

			return -1;

		}


		public void addToQueue(LinkedList<Pair<String,Integer>> queue, HashSet<String> dead, StringBuilder sb, int steps, HashSet<String> seen) {
			String s = sb.toString();
			if (!dead.contains(s) && !seen.contains(s)) {
				queue.offer(new Pair(s, steps));
				seen.add(s);
			}
		}
}

