func openLock(deadends []string, target string) int {   
    dead := make(map[int]struct{})
    for _, v := range deadends {
        n, _ := strconv.Atoi(v)
        dead[n] = struct{}{}
    }

    tar, _ := strconv.Atoi(target)

    offset := []func(int)int{}

    a := 1
    for i := 0; i < 4; i++ {
        b := a
        f1 := func(j int) int {
            if j / b % 10 == 9 {
                return j - b * 9
            } else {
                return j + b
            }
        }
        f2 := func(j int) int {
            if j / b % 10 == 0 {
                return j + b * 9
            } else {
                return j - b
            }
        }
        offset = append(offset, f1)
        offset = append(offset, f2)
        a *= 10
    }

    set := make(map[int]struct{})
    queue := []int{0}
    set[0] = struct{}{}
    res := 0
    for len(queue) > 0 {
        l := len(queue)
        for i := 0; i < l; i++ {
            if _, ok := dead[queue[i]]; ok {
                continue
            }
            if queue[i] == tar {
                return res
            }
            for _, f := range offset {
                newnum := f(queue[i])
                if _, ok := set[newnum]; ok {
