class Solution {
    public int openLock(String[] deadends, String target) {
        Set<String> deadendsSet = new HashSet<>(Arrays.asList(deadends));

        Queue<String> q = new ArrayDeque<>();

        Set<String> seen = new HashSet<>();

        if (!deadendsSet.contains("0000")) {
            q.offer("0000");    
            seen.add("0000");
        }

        int layer = 0;

        while (!q.isEmpty()) { 
            int size = q.size();

            for (int i = 0; i < size; i++) {
                String cur = q.poll();

                if (cur.equals(target)) {
                    return layer;
                }

                for (String adj: getAdj(cur)) {
                    if (!deadendsSet.contains(adj) && !seen.contains(adj)) {
                        q.offer(adj);
                        seen.add(adj);
                    }
                }
            }

            layer++;
        }

        return -1;
    }

    List<String> getAdj(String cur) {
        List<String> r = new ArrayList<>();

        // rotate left or right
        for (int i = 0; i < cur.length(); i++) {
            int wheelValue = Character.getNumericValue(cur.charAt(i));

            r.add(buildNextWheel(wheelValue + 1 > 9 ? 0 : wheelValue + 1, cur, i));

            r.add(buildNextWheel(wheelValue - 1 < 0 ? 9 : wheelValue - 1, cur, i));
        }

        return r;
    }

    String buildNextWheel(int wheelValue, String cur, int i) {
        StringBuilder sb = new StringBuilder(cur);
        sb.setCharAt(i, (char)(wheelValue + '0'));
        return sb.toString();
    }
}
