                Q.pop();
                if(value == key) return count;
                if(visited[value]) continue;
                visited[value] = true;
                for(int j=1; j<=1000; j *=10) {
                    Q.push(value + ((value / j + 1) % 10 - value / j % 10) * j);
                    Q.push(value + ((value / j + 9) % 10 - value / j % 10) * j);
                }
            }
            count++;
        }
        return -1;
    }
}
