use std::collections::{HashSet, VecDeque};
use std::iter::FromIterator;


impl Solution {
    pub fn open_lock(deadends: Vec<String>, target: String) -> i32 {
        let target = target.parse::<u32>().unwrap();
        let mut visited = HashSet::<u32>::from_iter(deadends.iter().map(|x| x.parse::<u32>().unwrap()));

        let mut q = VecDeque::from(vec![0u32]);

        let mut step = 0;
        while !q.is_empty() {
            for _ in 0..q.len() {
                let x = q.pop_front().unwrap();
                if visited.contains(&x) { continue; }
                if x == target {
                    return step;
                }
                for n in &[1, 10, 100, 1000] {
                    let cur = x / n % 10;
                    q.push_back(x - cur * n + (cur + 11) % 10 * n);
                    q.push_back(x - cur * n + (cur + 9) % 10 * n);
                }
                visited.insert(x);
            }
            step += 1;
        }
        -1
    }
}
