```java
/**
* Time Complexity: where N is the length of lock (4 in our case):
*
* O(N) for enumerating neighbors,
* O(digits^N) (10^4 in this case neighbors for each node)
* O(D) for initializing deadends set
* So time complexity is O(N*digits^N + D)
*
* Space complexity: O(digits^N + D) or the queue and the set dead
*/
class Solution {
    public int openLock(String[] deadends, String target) {
        HashSet<String> deadEndsSet = new HashSet<>();
        for (String deadend: deadends)
            deadEndsSet.add(deadend);

        if (deadEndsSet.contains("0000"))
            return -1;

        HashSet<String> visited = new HashSet<>();
        Deque<String> queue = new LinkedList<>();

        queue.addLast("0000");
        queue.addLast(null);
        visited.add("0000");

        int level = 0;
        while (!queue.isEmpty()) {
            String currentCombination = queue.pollFirst();
            if (currentCombination == null) {
                level++;
				// This is important to check 
                if (queue.peek() != null)
                    queue.addLast(null);
                continue;
            }

            for (int pos = 0; pos < 4; pos++) {
                String forwardComb = generateNextNumber(currentCombination, pos, true);
                String backwardComb = generateNextNumber(currentCombination, pos, false);
                // System.out.println(currentCombination + " -> " + forwardComb);
                // System.out.println(currentCombination + " -> " + backwardComb);
                if(forwardComb.equals(target) || backwardComb.equals(target)) {
                    return level + 1;
                }

                if (!deadEndsSet.contains(forwardComb) && !visited.contains(forwardComb)) {
                    visited.add(forwardComb);
                    queue.addLast(forwardComb);
                } 

                if (!deadEndsSet.contains(backwardComb) && !visited.contains(backwardComb)) {
                    visited.add(backwardComb);
                    queue.addLast(backwardComb);
                } 
            }
        }

        return -1;
    }

    private String generateNextNumber(String number, int position, boolean forwardRotation) {
        char[] numberArr = number.toCharArray();
        if ((numberArr[position] - '0') == 0 && !forwardRotation) {
            numberArr[position] = '9';
        } else if ((numberArr[position] - '0') == 9 && forwardRotation) {
            numberArr[position] = '0';
        } else {
            if (forwardRotation)
                numberArr[position] = (char) (((numberArr[position] - '0') + 1) + '0');
            else 
                numberArr[position] = (char) (((numberArr[position] - '0') - 1) + '0');    
        }

        return new String(numberArr);
    }
}
