class Solution {
    public int openLock(String[] deadends, String target) {
        Set<String> visited = new HashSet<>();
        Set<String> deadSet = new HashSet<>(Arrays.asList(deadends)); 
        if (deadSet.contains("0000")) return -1;
        int len = 0;

        Deque<String> queue = new LinkedList<>();
        visited.add("0000");
        queue.offer("0000");

        while (!queue.isEmpty()) {
            int size = queue.size();
            while (size-- > 0) {
                String cur = queue.poll();
                for (String next : getNextLevel(cur)) {
                    if (!visited.contains(next) && !deadSet.contains(next)) {
                        if (next.equals(target)) return len + 1;
                        visited.add(next);
                        queue.offer(next);
                    }
                }
            }
            len++;
        }
        return -1;

    }

    private List<String> getNextLevel(String s) {
		List<String> res = new ArrayList<>();
		char[] ca = s.toCharArray();
		for (int i = 0; i < 4; i++) {
			char c = ca[i];
			ca[i] = c == '9' ? '0' : (char) (c + 1);
			res.add(new String(ca));
			ca[i] = c; // restore ca
			ca[i] = c == '0' ? '9' : (char) (c - 1);
			res.add(new String(ca));
			ca[i] = c; // restore ca
		}
		return res;
	}
}
