class Solution {
public:
    // returns a vector containing all the possible combination from a string
    vector<string> gen(string s)
    {
        vector<string> t;
        for(int i=0;i<4;i++)
        {
            string temp = s;
            temp[i] = (s[i]-'0' + 1) % 10 + '0';
            t.push_back(temp);
            temp[i] = (s[i]-'0' -1 + 10) % 10 + '0';
            t.push_back(temp);                  
        }
        return t;
    }
    // return distance if we have a common element in map
    int fn(map<string,int> &m, map<string,int> &n)
    {
        for(auto x : m)
        {
            if(n.find(x.first)!=n.end()){
                return m[x.first] + n[x.first];}
        }
        return -1;
    }
	//performing Bfs for one complete level
    void bidirBfs(queue<pair<string,int>> &q, map<string,int> &m, unordered_set<string> &dds)
    {
        auto temp = q.front();
        q.pop();
        while(temp.first!="-")
        {
            vector<string> fo = gen(temp.first);
            for(auto x : fo)
            {
                if(dds.find(x)!=dds.end())
                    continue;
                if(m.find(x)==m.end())
                {
                    q.push({x,temp.second+1});
                    m[x] = temp.second+1;
                }
            }
            temp = q.front(); q.pop();
        }
        if(!q.empty())
        {
            q.push({"-",-1});
        }
    }
    int openLock(vector<string>& deadends, string target) {
        // set to know if a combination is deadend or not
        unordered_set<string> dds(deadends.begin(),deadends.end());
        if(dds.find("0000")!=dds.end()) return -1;

        // two maps to store combination that we made already with their distance from source
        map<string,int> smap,tmap;
        smap["0000"] = 0; tmap[target] = 0;

        // queue to perform BFS
        queue<pair<string,int>> sq,tq;
        sq.push({"0000",0}); sq.push({"-",-1}); // queue for source
        tq.push({target,0}); tq.push({"-",-1}); // queue for target

        // performing bidirectional Bfs from both ends
        while(!sq.empty() && !tq.empty())
        {
            bidirBfs(sq,smap,dds);//search from source
            bidirBfs(tq,tmap,dds);//search from target
            // then checking if we reached a common point
            int ans = fn(smap,tmap);
            if(ans!=-1) return ans;
        }
        return -1;
    }
};
