class Solution {
public:
    int openLock(vector<string>& deadends, string target) {
        unordered_set<string> sets;
        for(auto i : deadends)
        {
            sets.insert(i);
        }
        string begin = "0000";
        if(sets.count(begin))
        {
            return -1;
        }
        // 这一题其实说难不难,说简单也不简单,核心思想是,如何处理这个问题,这个相当于是图论的问题,就是迷宫问题,我给了你
        //障碍物,如何处理这个问题,解决主要的核心思想,其实这个可以看做这个地图是一条线,我们可以在这个线上走一步,这个线长总共是四个
        queue<pair<string,int>> q;
        q.push(pair<string,int>(begin,0));
        while(!q.empty())
        {
            auto t = q.front();
            if(t.first == target)
            {
               return t.second;
            }
            q.pop();
            for(int i = 0;i<t.first.size();i++)
            {
                for(int j = -1;j<=1;j+=2)
                {
                    string temp = t.first;
                    if(j == -1 && temp[i] =='0')
                    {
                        temp[i] = '9';
                    }
                    else if(j==1 && temp[i] == '9')
                    {
                        temp[i] = '0';
                    }
                    else
                    {
                        temp[i] = temp[i]+j;
                    }
                    //处理数据
                    if(sets.count(temp))
                    {
                        continue;
                    }
                    else
                    {
                        q.push(pair<string,int>(temp,t.second+1));
                        sets.insert(temp);
                    }
                }
            }
        }
        return -1;
    }
};
