class Solution {
    func openLock(_ deadends: [String], _ target: String) -> Int {
        var visited = Set(deadends)
        var queue = [(String, Int)]()
        var queueStart = 0

        let zeroAscii = Character("0").asciiValue!
        let code = "0000"
        if !visited.contains(code) {
            queue.append((code, 0))
            visited.insert(code)
        }
        while queueStart != queue.count {
            let (currentCode, currentDeapth) = queue[queueStart]
            if currentCode == target {
                return currentDeapth
            }
            queueStart += 1

            let currentCodeArray = Array(currentCode)
            for (i, char) in currentCodeArray.enumerated() {
                let digit = char.asciiValue! - zeroAscii
                let nextDigit = (digit + 1) % 10
                let prevDigit = digit > 0 ? digit - 1 : 9
                for digit in [nextDigit, prevDigit] {
                    let nextDigitChar = Character(UnicodeScalar(digit + zeroAscii))
                    var nextCodeArray = currentCodeArray
                    nextCodeArray[i] = nextDigitChar
                    let nextCode = String(nextCodeArray)
                    if !visited.contains(nextCode) {
                        visited.insert(nextCode)
                        queue.append((nextCode, currentDeapth+1))
                    }
                }
            }
        }

        return -1
    }
}
