/*explantion:

-> queue is used to store string and moves needed to reach that combination.
-> we use set to save the deadends and visited string
-> if strings created after modifications are present in deadends are ignored and not saved in queue
-> if the newly created string is the target then it took (move + 1) moves to reach there.

*/

class Solution {
public:
            int openLock(vector<string>& deadends, string target) {
            queue<pair<string,int>>q;
            unordered_set<string>s,visited;
            for(auto it:deadends) s.insert(it);
            if(s.find("0000") != s.end() || s.find(target) != s.end()) return -1;
            q.push({"0000",0});
            if("0000" == target) return 0;

            while(!q.empty()){
              auto [num,move] = q.front(); q.pop();  

              for(int i = 0; i < 4; ++i){
                  char temp = num[i];
                  int curr = num[i] - '0';
                  int next = (curr + 1)%10; //handles increment range [0-9] in ith pos
                  num[i] = next + '0';
                  if(target == num) return move + 1;

                  if(s.find(num) == s.end() && visited.find(num) == visited.end())
                      {
                        q.push({num,move+1});
                        visited.insert(num);
                      }

                  next = (curr -1 + 10)%10; //handles decrement range [0-9] in ith pos
                  num[i] = next + '0';
                  if(target == num) return move + 1;

                  if(s.find(num) == s.end() && visited.find(num) == visited.end())
                      {
                        q.push({num,move+1});
                        visited.insert(num);
                      }

                  num[i] = curr + '0';       
              }
            }
        return -1;
    }
};
