	}
	else{
		return rob(root->left,true)+rob(root->right,true);
	}
}
int rob(const TreeNode* root){
    if(root==nullptr){
		return 0;
	}
	return rob(root,true);
}

void Add_Leaves(TreeNode *root){
	if(root->left!=nullptr){ //root->right is automatically not null because we only use balanced trees
		Add_Leaves(root->left);
		Add_Leaves(root->right);
