        if(root->right)
        {
            grandchild += m[root->right->left];
            grandchild += m[root->right->right];
        }    

        m[root] = max(root->val + grandchild , child);    
        return m[root];        
    }

    int rob(TreeNode* root) {

        m.clear();
        if(!root)
            return 0;

        return func(root);
    }
};

