class Solution {
public:
	int rob(TreeNode* root) 
	{
		int mt, ms;
		rob(root, mt, ms);
		return max(mt, ms);
	}

	void rob(TreeNode* node, int& mt, int& ms)
	{
		mt = ms = 0;
		if (!node) return;

		int mtl, mtr, msl, msr;
		rob(node->left, mtl, msl);
		rob(node->right, mtr, msr);

		mt = msl + msr + node->val;
		ms = max(mtl, msl) + max(mtr, msr);
		return;
	}
};

