class Solution {
public:
    int rob(TreeNode* root) {
        vector<int> vect;
        queue<TreeNode*> q;
        if(root==NULL) return 0;
        q.push(root);
        while(q.size()>0){
            int n = q.size();
            vector<int> t;
            for(int i=0;i<n;i++){
                TreeNode* temp = q.front();
                q.pop();
                t.push_back(temp->val);
                if(temp->left) q.push(temp->left);
                if(temp->right) q.push(temp->right);
            }
            int temp = 0;
            for(auto item : t) temp+=item;
            vect.push_back(temp);
            temp = 0;
        }
        int odd = 0 , even =0 , finalAns = vect[0];
        for(int i=0;i<vect.size();i++){
            for(int k=i+2;k<vect.size();k+=2){
                odd += vect[k];
