            if(odd+vect[i] >finalAns){
                finalAns = odd+vect[i];
            }
            if(even+vect[i] > finalAns){
                finalAns = even+vect[i];
            }
            odd = 0;
            even = 0;
        }
        return finalAns;
    }
};
