class Solution:
    def rob(self, root: TreeNode) -> int:
        """
        1. Each node returns the maximum amount of money that includes the current node and 
        the max that excludes the curent node. 
        2. The max that excludes the current node can then 
        be included in the parents calculations.
        """
        Money = collections.namedtuple('Money', ['skip_child', 'with_child'])

        def rob_recursive(node):
            if not node:
                return Money(0, 0)

            left = rob_recursive(node.left)
            right = rob_recursive(node.right)

            return Money(max(left) + max(right),
                         node.val + left.skip_child + right.skip_child)

        return max(rob_recursive(root))
