class Solution {
public:
    map<TreeNode* ,int> mp;

    int solve(TreeNode *root){
        if(!root)
            return 0;

        if(mp[root])
            return mp[root];

        //case1: taking root node then we can only take its grandparents
        int withnode = root->val;
        if(root->left){
            withnode += solve(root->left->left);
            withnode += solve(root->left->right);
        }

        if(root->right){
            withnode += solve(root->right->left);
            withnode += solve(root->right->right);
        }

        //case 2: without root node we can take its child
        int withoutnode = solve(root->left)+ solve(root->right);

        return mp[root] = max(withnode, withoutnode);
    }    

    int rob(TreeNode* root) {
        return solve(root);        
    }
};
