/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    unordered_map<TreeNode*,unordered_map<bool,int>> map;
    int solve(TreeNode* root, bool can){
        if(!root) return 0;
        if(map.find(root)!=map.end() && map[root].find(can)!=map[root].end()) return map[root][can];
        int inc=0, exc=0;
        if(can) inc+=root->val + solve(root->left,false) + solve(root->right,false);
        exc += solve(root->left,true) + solve(root->right,true);
        return map[root][can]=max(inc,exc);
    }

    int rob(TreeNode* root) {
        return solve(root, true);
    }
};
```
