    public int rob(TreeNode root) {
        MaxRobTillHere pair = maxRob(root);
        return max(pair.including, pair.excluding);
    }

    private MaxRobTillHere maxRob(TreeNode node) {
        if(node == null) {
            return new MaxRobTillHere(0,0);
        }        
        MaxRobTillHere left = maxRob(node.left);
        MaxRobTillHere right = maxRob(node.right);

        int maxIncludingCurrent = left.excluding + right.excluding + node.val;
        int maxExcludingCurrent = max(left.including, left.excluding) + 
                                  max(right.including, right.excluding);

        MaxRobTillHere robbed = new MaxRobTillHere(maxIncludingCurrent, maxExcludingCurrent);

        return robbed;
    }

    private int max(int ... a) {
        int max = 0;
        for(int i : a) {
            max = max < i ? i : max;
        }
        return max;
    }
}
