class Solution {
public:
unordered_map<TreeNode*, int> table;
	int rob(TreeNode* root) {
		return profit(root);
	}

	int profit(TreeNode* root) {
		if (table.count(root))
			return table[root];
		if (root == NULL)
			return 0;
		if (root->left == NULL && root->right == NULL)
			return root->val;
		int c, d, e, f;
		int l1 = profit(root->left) + profit(root->right);
		c = root->left != NULL ? profit(root->left->left) : 0;
		d = root->left != NULL ? profit(root->left->right) : 0;
		e = root->right != NULL ? profit(root->right->left) : 0;
		f = root->right != NULL ? profit(root->right->right) : 0;
		int l2 = root->val + c + d + e + f;
		return table[root] = max (l1, l2);
	}
};

