```
class Solution {
public:
    bool isToeplitzMatrix(vector<vector<int>>& matrix) {
        if(matrix.size() == 1) {
            return true;
        }
        int i = matrix.size() - 2;
        int j = 0;


         while(i != matrix.size() - 1 && j != matrix[0].size() - 1) {
            int row = i;
            int col = j;

            int num = matrix[row++][col++];
            while(row < matrix.size() && col < matrix[0].size()) {
                if(matrix[row][col] != num) {
                    return false;
                }
                row++;
                col++;
            }

            if(i != 0) {
                i--;
            } else {
                j++;
            }
        }
        return true;
    }
};
```

