    public boolean isToeplitzMatrix(int[][] matrix) {

        int n = matrix.length , m = matrix[0].length;
        // loop from (column - 1) to row to check all the diagonals from top left to bottom right

        for( int i = -(m - 1); i < n; i++ ) {
            // row and col initialized as per the conditions.
            int row = ( i < 0 ) ? 0 : i;
            int col = ( i < 0 ) ? -i : 0;
            int prev = -1;

            for( ; row < n && col < m; row++,col++ ) {

                if( prev != -1 && prev!=matrix[row][col] ) return false;
                prev = matrix[row][col];

            }
        }

        return true;
    }
}
