class Solution {
public:
    bool isToeplitzMatrix(vector<vector<int>>& matrix) {
        int m=matrix.size();
            int n=matrix[0].size();
    if(m<=1 || n<=1)return true;
        // int i=m-1;
        // int j=0;
        for(int i=m-1;i>=0;i--){
           int j=0;
            int i1=i;
            int t=matrix[i][j];
            while(j<=n-1 && i1<=m-1){
                if(matrix[i1][j]!=t)return false;
                j++;
                i1++;
            }
        }
        for(int j=0;j<n;j++){
           int i=0;
            int j1=j;
            int t=matrix[i][j];
            while(j1<=n-1 && i<=m-1){
                if(matrix[i][j1]!=t)return false;
                j1++;
                i++;
            }
        }
        return true;
    }
};

