/**
 * @param {number[][]} matrix
 * @return {boolean}
 */
var isToeplitzMatrix = function(matrix) {
    if(matrix.length == 1 ) return true;
    return checkHorizontal(matrix) && checkVertical(matrix)
}

var checkVertical  = (matrix) => {
    if(matrix.length == 1 ) return true;
    for(let i = matrix.length - 1; i >= 0; i--){
        let num = matrix[i][0];
        let j = i + 1;
        let p = 1;
        while(matrix[j] && matrix[j][p] != undefined) {
            if(num^matrix[j][p]){
                return false;
            }
            else {
                j++;
                p++;
            }
        }
    }
    return true;
}

var checkHorizontal = (matrix) => {
if(matrix.length == 1 ) return true;
    for(let i = 1; i < matrix[0].length; i++){
        let num = matrix[0][i];
        let j = i + 1;
        let p = 1;
        while(matrix[p] && matrix[p][j] != undefined) {
            if(num^matrix[p][j]) {
                return false;
            }
            else {
                p++;
                j++;
            }
        }
    }
    return true;
}
