class Solution:
    def isToeplitzMatrix(self, matrix: List[List[int]]) -> bool:
        # Corner case
        if not matrix or len(matrix) == 0 or len(matrix[0]) == 0:
            return True
        rows, cols = len(matrix), len(matrix[0])
        return self.isleftDiagonal(matrix, rows, cols, 0, 0) and self.isrightDiagonal(matrix, rows, cols, 0, 0)

    def isleftDiagonal(self, matrix, rows, cols, i, j) -> bool:
        # Base case
        if i == rows or j == cols:
            return True
        # Recursion Rule
        value = matrix[i][j]
        m, n = i, j
        while i < rows and j < cols:
            if value != matrix[i][j]:
                return False
            i += 1
            j += 1
        return self.isleftDiagonal(matrix, rows, cols, m + 1, n)

    def isrightDiagonal(self, matrix, rows, cols, i, j) -> bool:
        # Base case
        if i == rows or j == cols:
            return True
        # Recursion Rule
        value = matrix[i][j]
        m, n = i, j
        while i < rows and j < cols:
            if value != matrix[i][j]:
                return False
            i += 1
            j += 1
        return self.isrightDiagonal(matrix, rows, cols, m, n + 1)
