class Solution {
public:
    bool isToeplitzMatrix(vector<vector<int>>& matrix) {
        int row = matrix.size();
        int col = matrix[0].size();
        int head = 0;
        int i = 0;
        for(int j = col - 1; j >= 0; j--){
            head = matrix[i][j];
            int k = j + 1;
            int z = i + 1;
            while((k < col) && (z < row)){
                if(matrix[z][k] == head){
                    k++;
                    z++;
                }
                else{
                    return false;
                }
            }
        }
        int j = 0;
        for(int n = 1; n < row; n++){
            head = matrix[n][j];
            int k = j + 1;
            int z = n + 1;
            while((z < row) && (k < col)){
                if(matrix[z][k] == head){
                    k++;
                    z++;
                }
                else{
                    return false;
                }
            }
        }
        return true;
    }
};
