const plusOne = (digits) => {
  const last = digits.length - 1;
  digits[last]++;
  for (let i = last; i > 0 && digits[i] === 10; i--) {
    digits[i] = 0;
    digits[i - 1]++;
  }
  if (digits[0] === 10) {
    digits[0] = 0;
    digits.unshift(1);
  }
  return digits;
};
