public class Solution {
    public int[] PlusOne(int[] digits) {
       for(int i = digits.Length - 1; i >= 0; i--) {
           if (digits[i] == 9) {
               digits[i] = 0;
           } else {
               digits[i]++;
               return digits;
           }
       } 
       int[] firstOne = new int[] { 1 };
       return firstOne.Concat(digits).ToArray();
    }
}
