class Solution {

public:
    vector<int> plusOne(vector<int>& digits) {
        vector<int> ans;
//So we make the another vector which have value 1.
        vector<int> z={1};
//there may be the carry after adding .
        int carry=0;
        int i=digits.size()-1;
        int j=z.size()-1;
//we have to start from end 
        while(i>=0 && j>=0)
        {
            int sum=digits[i]+z[j]+carry;
            carry=sum/10;
            sum=sum%10;
            ans.push_back(sum);
            i--;
            j--;
        }
 // after adding 1 some part other vector is left and some carry is also left.
        while(i>=0)
        {
            int sum = digits[i] + carry;
            carry = sum/10;
            sum=sum%10;
            ans.push_back(sum);
            i--;
        }
//there may be carry after ending also , so that carry is also added. Ex- [9,9]+[1]=[1,0,0]
        while(carry!=0)
        {
            int sum = carry;
            carry=sum/10;
            sum=sum%10;
            ans.push_back(sum);
        }

 //at last we have to reverse the vector. If we not reverse the vector ans will come wrong.
//Ex-  [9,9,9,1]+[1]=[2,9,9,9]--->wrong   
        reverse(ans.begin() , ans.end());

        return ans;

    }
};

