class Solution {
public:
    vector<int> plusOne(vector<int>& digits) {
 int l= digits.size();
 for(int i=l-1; i>=0; i--)
 {
     if(digits[i]<9)
     {
        digits[i]++;
        return digits;

     }
     else
     {
         digits[i]=0;
     }

 }
  digits.push_back(0);
     digits[0]=1;
     return digits;
    }
};

class Solution:
    def plusOne(self, digits: List[int]) -> List[int]:
        number = 0
        n = len(digits)
        ans = []
        if len(digits) >= 1 and len(digits) <= 100:
            for i in range(len(digits)):
                number += digits[i] * (10 ** (n - 1))
                n -= 1
            number += 1
            while number > 0:
                digit = number % 10
                number = number // 10
                ans.append(digit)
            ans.reverse()
            return ans

