public class Solution {
    public string FractionToDecimal(int numerator, int denominator) {
        return FractionToDecimalLong((long)numerator, (long)denominator);
    }

    private string FractionToDecimalLong(long numerator, long denominator) {
        var isNegative = false;
        if ((numerator > 0 && denominator < 0) || (numerator < 0 && denominator > 0)) {
            isNegative = true;
        }

        numerator = Math.Abs(numerator);
        denominator = Math.Abs(denominator);

        var integerPart = numerator / denominator;

        var floatPart = "";

        var remainderAndIndex = new Dictionary<long, int>();
        var remainder = numerator % denominator;

        if (remainder == 0) return isNegative ? $"-{integerPart}" : $"{integerPart}";

        var i = 0;

        while (remainder != 0) {
            remainderAndIndex[remainder] = i;
            i++;
            remainder *= 10;
            floatPart = $"{floatPart}{remainder / denominator}";
            remainder = remainder % denominator;
            if (remainderAndIndex.ContainsKey(remainder)) {
                floatPart = floatPart.Insert(remainderAndIndex[remainder], "(");
                floatPart += ")";
                break;
            }
        }

        if (isNegative) {
            return $"-{integerPart}.{floatPart}";
        }

        return $"{integerPart}.{floatPart}";
    }
}
