            if (seen[numerator]) {
                decimal.insert(seen[numerator], "(");
                decimal += ")";
                break;
            }
            seen[numerator] = decimal.size();
        }

        if (decimal.size() == 1) return integer;
        else return integer + decimal;
    }
};
