var fractionToDecimal = function(numerator, denominator) {
    if(numerator === 0) {
        return '0';
    }
	// check if the result should be negative or not
    const isNeg = (numerator < 0 && denominator > 0) || (denominator < 0 && numerator > 0);
    numerator = Math.abs(numerator);
    denominator = Math.abs(denominator);

	// this map will keep note of all the remainders occurences,
	// if a remainder appears again, that will repeat the same pattern
    const remaindersMap = new Map();

	// add the negative sign to result if applicable
    let result = isNeg ? '-' : '';
    let quotient = numerator / denominator,
        remainder = numerator % denominator;

	// add the quotient to the result -
	// if num > deno the it will be a positive integer, else it will be 0
    result += Math.floor(quotient);

	// if remainder is 0, that means the num is entirely divisible by deno, so we can return the result
    if(remainder === 0) {
        return result;
    } else {
		// else there will be a floating value, so we add decimal to the resultant string
		// set num to remainder
		// add the remainder to map to check if it reappears
        result += '.';
        numerator = remainder;
        remaindersMap.set(remainder, 0);
    }
	// handling the floating point separately, `decimalStr` will store that string
	// `patternStartIdx` - to keep note of the index from where pattern will start
    let decimalStr = '',
        patternStartIdx = -1;

	// 2 conditions for getting out of the loop
	// num > 0 - when num === 0 that means num is divisible by deno
	// patternStartIdx === -1 - it will change when a pattern  is found
    while(numerator > 0 && patternStartIdx === -1) {
        numerator *= 10;    // numerator < denominator

        quotient = numerator / denominator;
        remainder = numerator % denominator;

		// add the quotient to the decimalStr
        decimalStr += Math.floor(quotient);

		// if remainder is reoccuring => pattern found
        if(remaindersMap.has(remainder)) {
			// get the start index from where the pattern begin - it will be the index the remainder had previous occured
			// we get the value and break from the loop
            patternStartIdx = remaindersMap.get(remainder);
            break;
        }
		// else add the remainder to the map with index as `decimalStr.length`
		// and set num as the remainder for next iteration
        remaindersMap.set(remainder, decimalStr.length);
        numerator = remainder;
    }
	// if pattern is found, add the decimal string along with brackets around the repeating pattern
	// else simply add the decimalStr
    if(patternStartIdx !== -1) {
        result += decimalStr.substring(0, patternStartIdx);
        result += '(' + decimalStr.substring(patternStartIdx) + ')';    
    } else {
        result += decimalStr;
    }
    return result;
}
```
Time Complexity = O(denominator)
Space Complexity = O(denominator)

The range of remainder can be from [0, denominator - 1]
We loop till remainder reoccurs, max it can check for each remainder and we are storing remainder values in a map.

