const fractionToDecimal = (numerator, denominator) => {
    if(numerator === 0) return "0";
    if(denominator === 0) return numerator < 0 ? Number.NEGATIVE_INFINITY:Number.POSITIVE_INFINITY;

    let res = "";

    //Check result sign
    if(Math.sign(numerator) !== Math.sign(denominator)) res += "-";

    let n = Math.abs(numerator);
    const d = Math.abs(denominator);

    //don't use n/d | 0 to floor since js bitwise operation is based on 32 bits
    //and large positive num could cause sign bit to flip into a neg num or overflow
    res += Math.floor(n/d); 
    n = n%d;

    if(n === 0) return res;

    res += ".";

    //key for remainder, value for pattern start index
    let map = new Map();   // USE MAP

    //we can use res.length as index since it keeps track of the growth
    //alwasy being the next index to insert so we don't need another variable!
    while(n !== 0){
        //keep track of remainders
        map.set(n,res.length);

        n *= 10;
        res += Math.floor(n/d); //add quotient digit to the res.length index
        n %= d;

        if(map.has(n))
            return `${res.substring(0,map.get(n))}(${res.substr(map.get(n))})`;
    }
    return res;
};
```

