class Solution {
public:
    // Updated the signature to int64_t to handle specific cases like abs(INT_MIN)
    string fractionToDecimal(int64_t numerator, int64_t denominator) {
        // special case
        if (numerator == 0) return "0";
        // we will use string stream to store the result
        stringstream res;        
        // if negative, we will add "-"
        if (numerator > 0 ^ denominator > 0) 
            res << "-";

		// working now with absolute values
        numerator = abs(numerator);
        denominator = abs(denominator);

        // add the int result.
        res << (numerator/denominator);

        // working with the remainder 
        numerator = numerator % denominator;

        // if we have a fraction, we need to add a "."
        if (numerator > 0) 
            res << ".";

        unordered_set<int> dups;        
        bool re = false;
        stringstream reStream;
        while (numerator != 0) {
            if (dups.count(numerator) > 0) {
				// we have a repeat, let's identify the repeated fraction or break if have already found it.
                if (!re) {
					// let's find out the repeated fraction
					re = true;
					// clear the map for the next repeated segment.
                    dups.clear();
                } else { 
                    break; 
                }
            }
			// keep track of numerator values to detect the next duplicated fraction if it exists
            dups.insert(numerator);

            numerator *= 10;
            if (!re) {
                // non repeated fraction stream
                res << (numerator / denominator);
            } else {
                // repeated fraction stream.
                reStream << (numerator / denominator);
            }
            // working with the remainder of numerator
            numerator = numerator % denominator;
        }

        // in case we have a repeated fraction
        string repeatedFraction = reStream.str();
        if (!repeatedFraction.empty()) {
            string ans = res.str();
            // find the start position of the repeat
            auto repeat = ans.find(repeatedFraction);
            // reset the stream
            res.str(std::string());
            res << ans.substr(0, repeat) << "(" << ans.substr(repeat) << ")";
        }
        return res.str();
    }
};
