public:
    string fractionToDecimal(int numerator, int denominator) {

        long long  n = numerator;
        long long  d = denominator;

        if(!n)return "0";

        string sign = ((n/abs(n)) != (d/abs(d)))?"-":"";

        n = abs(n);
        d = abs(d);


        string ans = to_string(n/d);

