            n = n%d;
            if(mp[n]){
                string s1 = ans.substr(0,mp[n]);
                string s2 ="(" + ans.substr(mp[n]) + ")";
                return sign + s1 + s2;
            }

            mp[n] = ans.size();
        }

        return sign + ans;



    }
};
