public class Solution {
    public string FractionToDecimal(int numerator, int denominator)
    {
        if (denominator == 0)
        {
            //throw new IllegalArgumentException(" denominator can not be zero");
            throw new ArgumentException(" denominator can not be zero");
        }

        // avoid overflow for minimum value of integer
        long newNum = (long)numerator;
        long newDeNom = (long)denominator;

        StringBuilder sb = new StringBuilder();

        // detect sign
        if ((newNum > 0 && newDeNom < 0) || (newNum < 0 && newDeNom > 0))
            sb.Append("-");

        // make sure it is postive value
        newNum = Math.Abs(newNum);
        newDeNom = Math.Abs(newDeNom);      

        sb.Append(newNum / newDeNom);

        long remainder = newNum % newDeNom;
        Dictionary<long, int> remainderMap = new Dictionary<long, int>();

        if (remainder != 0)
            sb.Append(".");

        while (remainder != 0 && !remainderMap.ContainsKey(remainder))
        {
            remainderMap.Add(remainder, sb.Length);
            remainder *= 10;
            sb.Append(remainder / newDeNom);
            remainder = remainder % newDeNom; 
        }

        if (remainderMap.ContainsKey(remainder))
        {
            sb.Insert(remainderMap[remainder], "(");
            sb.Append(")");
        }
        return sb.ToString();
    }    
}
