class Solution:
# @param {integer} numerator
# @param {integer} denominator
# @return {string}
def fractionToDecimal(self, numerator, denominator):
    neg = False
    if numerator<0 and denominator>0 or numerator>0 and denominator<0:
        neg = True

    numerator = abs(numerator)
    denominator = abs(denominator)
    ans = str(numerator/denominator)
    if neg:
        ans = '-' + ans

    if numerator%denominator == 0:
        return ans

    dic = {}
    ans += '.'
    count = len(ans)


    tmp = numerator%denominator
    while True:
        if tmp not in dic:
            dic[tmp] = len(ans)
        else:
            ans = ans[:dic[tmp]] + '(' + ans[dic[tmp]:] + ')'
            break

        tmp *= 10
        if tmp < denominator:
            ans += '0'
            continue
        if tmp%denominator == 0:
            ans += str(tmp/denominator)
            break
        else:
            tmpans = tmp/denominator
            tmp = tmp%denominator
            ans += str(tmpans)

    return ans

class Solution {
public:
	string fractionToDecimal(int numerator, int denominator) {
		ostringstream os;
            //dealing with denominator == 0
		if (denominator == 0){
			os << INT_MAX;
			return os.str();
		}
            //dealing with numerator == 0
		if(numerator==0)
		    return "0";
            //others
		if ((numerator<0) ^ (denominator < 0))
			os << '-';
		long long x = abs((long long)numerator), y = abs((long long)denominator);
		os << x / y;
		if ((x %= y) == 0)
			return os.str();
		os << '.';

	    long long ax = x, ay = y;
		int i = 0, j = 0;
           //find the count of non_repeating part,it depends on how many 2 or 5 factor 
           // are there in denominator
		while (y % 2 == 0){
			y /= 2;
			++i;
		}
		while (y % 5 == 0){
			y /= 5;
			++j;
		}
		int k = max(i, j);
            //non_repeating part
		while (k != 0 && ax != 0){
			ax = ax * 10;
			os << ax / ay;
			ax %= ay;
			--k;
		}
		if (ax == 0)
			return os.str();
		long long m = ax;
		os << '(';
            //repeating part
            //to find the repeating part ,we need to find when the denominator(here it is ax) repeats,
            //then we stop;
		while (true){
			ax = ax * 10;
			os << ax / ay;
			if ((ax %= ay) == m)
				break;
		}
		os << ')';
		return os.str();
	}
};

