class Solution {
public:
    string fractionToDecimal(int num, int den) {
        if (den == 0 || num == 0) {
            return "0";
        }
        string res = ((num < 0) ^ (den < 0)) == 1 ? "-" : "";
        // store as long since INT_MAX might come
        // also no overflow needed since string for INT_MIN/-1
        long a = abs((long)num);
        long b = abs((long)den);
        ostringstream os;
        os << a/b;
        res += os.str();
        if (a%b == 0) {
            return res;
        }

        res += ".";
        unordered_map<int, int> pos_map;
        long x = a%b;
        string frac = "";
        while(x) {
            long d = (x*10)/b;
            ostringstream os;
            os << d;
            frac += os.str();
            pos_map[x] = frac.size()-1;
            x = (x*10)%b;
            if (pos_map.find(x) != pos_map.end()) {
                int i = pos_map[x];
                res += string(frac, 0, i) + "(" + string(frac, i, frac.size()-i) + ")";
                return res;
            }
        }

        return (res +  frac);
    }
};

