/**
 * @param {number} numerator
 * @param {number} denominator
 * @return {string}
 */
var fractionToDecimal = function(numerator, denominator) {
  if (numerator === 0) {
    return "0"; // Special case for numerator equal to zero
  }

  let result = "";

  // Handle sign
  if (Math.sign(numerator) !== Math.sign(denominator)) {
    result += "-";
  }

  // Convert to positive values
  const numer = Math.abs(numerator);
  const denom = Math.abs(denominator);

  // Integer part
  result += Math.floor(numer / denom);

  let remainder = numer % denom;
  if (remainder === 0) {
    return result; // No fractional part, return result
  }

  result += ".";

  const map = new Map(); // To track remainders and their positions

  while (remainder !== 0) {
    if (map.has(remainder)) {
      // Repeating pattern detected
      const index = map.get(remainder);
      result = result.slice(0, index) + "(" + result.slice(index) + ")";
      break;
    }

    map.set(remainder, result.length); // Store remainder and position

    remainder *= 10;
    result += Math.floor(remainder / denom);
    remainder %= denom;
  }

  return result;
};

