class Solution {
public:
    string fractionToDecimal(int numerator, int denominator) {
        // If numerator is zero then return 0;
        if (numerator == 0) 
            return "0";

        string res = "";

        // Maybe numerator & denominator can be negative
        if ((numerator < 0) ^ (denominator < 0)) 
            res += '-';

        /*
        // Also you can check for negative like
        if((numerator < 0 && denominator > 0) || (numerator > 0 && denominator < 0))
            res += '-';
        */   

        // Remove the negative sign from the numerator | denominator
        long dividend = abs((long) numerator);
        long divisor = abs((long) denominator);

        // Find the remainder
        long rem = dividend % divisor;

        // Add the numeric result
        res += to_string(dividend / divisor);

        // in case no fractional part
        if (rem == 0)
            return res;

        // If remainder is not 0 then they having fractional part so add the point for fraction
        res += '.';

        unordered_map<long, int> map;

        // Calculate division
        while(rem != 0) {

            // Check for repeating part, If exist then insert in ();
            if (map.count(rem) > 0) {
                res.insert(map[rem], 1, '(');
                res += ')';
                break;
            }

            map[rem] = res.size();

            rem *= 10;

            // Add the quotient in result
            res += to_string(rem / divisor);
            rem %= divisor;
        }

        return res;
    }
};
```
