  def fractionToDecimal(self, n, d):
    if n%d==0: return str(n // d)

    rdict, ds= {0:-1}, []

    sign, n, d = '-' if bool(n<0) ^ bool(d<0) else '', abs(n), abs(d)
    I, r = sign + str(n // d), n % d

    while r not in rdict:
      rdict[r] = len(ds)
